import React, { useState } from 'react';
import { Globe, TrendingUp, Users, Award } from 'lucide-react';

function Login({ onLogin }) {
  const [name, setName] = useState('');
  const [isAnimating, setIsAnimating] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim()) {
      setIsAnimating(true);
      setTimeout(() => {
        onLogin(name.trim());
      }, 300);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-5">
        <div className="max-w-2xl w-full">
          <div className="text-center mb-12 animate-slide-down">
            <div className="inline-flex items-center justify-center w-24 h-24 bg-gradient-to-br from-apple-blue to-blue-600 rounded-3xl mb-6 shadow-apple-lg">
              <Globe className="w-12 h-12 text-white" />
            </div>
            <h1 className="text-6xl font-bold text-apple-gray-900 mb-4 tracking-tight">
              Econo-Me
            </h1>
            <p className="text-2xl text-apple-gray-400 font-light tracking-tight">
              Werde zum Wirtschaftsführer deiner Nation
            </p>
          </div>

        <div className="max-w-md mx-auto">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-apple-gray-900 font-medium mb-3 text-lg">
                Dein Name
              </label>
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Gib deinen Namen ein..."
                className="input-apple text-lg"
                autoFocus
              />
            </div>
            <button 
              type="submit" 
              className={`btn-apple w-full text-lg py-4 ${isAnimating ? 'scale-95' : ''}`}
              disabled={!name.trim()}
            >
              Spiel starten
            </button>
          </form>

          <div className="mt-12 bg-apple-gray-50 rounded-apple p-8 border border-black/5">
            <h3 className="text-xl font-semibold text-apple-gray-900 mb-5 flex items-center gap-2">
              <Award className="w-6 h-6 text-apple-blue" />
              Spielziel
            </h3>
            <ul className="space-y-3">
              <li className="flex items-start gap-3 text-apple-gray-400">
                <TrendingUp className="w-5 h-5 text-apple-blue mt-0.5 flex-shrink-0" />
                <span>Führe dein Land zum wirtschaftlichen Erfolg</span>
              </li>
              <li className="flex items-start gap-3 text-apple-gray-400">
                <Users className="w-5 h-5 text-apple-green mt-0.5 flex-shrink-0" />
                <span>Manage Beziehungen zu anderen Nationen</span>
              </li>
              <li className="flex items-start gap-3 text-apple-gray-400">
                <Globe className="w-5 h-5 text-apple-orange mt-0.5 flex-shrink-0" />
                <span>Erreiche die höchste Punktzahl</span>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
