import React, { useState } from 'react';

function CountrySelection({ countries, onSelect }) {
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [hoveredCountry, setHoveredCountry] = useState(null);

  const getFlag = (countryId) => {
    const flags = {
      'Germany': 'üá©üá™',
      'USA': 'üá∫üá∏',
      'China': 'üá®üá≥',
      'Japan': 'üáØüáµ',
      'UK': 'üá¨üáß',
      'France': 'üá´üá∑'
    };
    return flags[countryId] || 'üè≥Ô∏è';
  };

  const getDifficulty = (country) => {
    const unemploymentScore = country.unemployment > 6 ? 'Schwer' : country.unemployment > 4 ? 'Mittel' : 'Leicht';
    const tradeScore = country.tradeSurplus < 0 ? 'Herausfordernd' : 'Stabil';

    if (unemploymentScore === 'Schwer' || country.tradeSurplus < -500) return { level: 'Schwer', color: 'text-apple-red' };
    if (unemploymentScore === 'Mittel') return { level: 'Mittel', color: 'text-apple-orange' };
    return { level: 'Leicht', color: 'text-apple-green' };
  };

  const handleSelect = (countryId) => {
    setSelectedCountry(countryId);
    setTimeout(() => {
      onSelect(countryId);
    }, 300);
  };

  return (
    <div className="min-h-screen p-5">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16 animate-slide-down">
          <h1 className="text-6xl font-bold text-apple-gray-900 mb-4 tracking-tight">
            üåç W√§hle dein Land
          </h1>
          <p className="text-2xl text-apple-gray-400 font-light tracking-tight">
            Jedes Land hat unterschiedliche St√§rken und Herausforderungen
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Object.entries(countries).map(([id, country], index) => {
            const difficulty = getDifficulty(country);
            const isSelected = selectedCountry === id;
            const isHovered = hoveredCountry === id;

            return (
              <div
                key={id}
                className={`card-apple-hover cursor-pointer transition-all duration-300 ${
                  isSelected ? 'ring-4 ring-apple-blue scale-105' : ''
                } ${isHovered ? 'scale-105' : ''}`}
                style={{ animationDelay: `${index * 50}ms` }}
                onClick={() => handleSelect(id)}
                onMouseEnter={() => setHoveredCountry(id)}
                onMouseLeave={() => setHoveredCountry(null)}
              >
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-3xl font-semibold text-apple-gray-900 tracking-tight">
                    {getFlag(id)} {country.name}
                  </h3>
                  <span className={`text-sm font-medium px-3 py-1 rounded-pill ${difficulty.color} bg-opacity-10`}>
                    {difficulty.level}
                  </span>
                </div>

                <div className="space-y-3 text-apple-gray-400">
                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm">
                    <span className="font-medium">BIP</span>
                    <span className="font-semibold text-apple-gray-900">${country.gdp} Mrd.</span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm">
                    <span className="font-medium">Bev√∂lkerung</span>
                    <span className="font-semibold text-apple-gray-900">{country.population} Mio.</span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm">
                    <span className="font-medium">Arbeitslosigkeit</span>
                    <span className={`font-semibold ${
                      country.unemployment > 6 ? 'text-apple-red' : 
                      country.unemployment > 4 ? 'text-apple-orange' : 
                      'text-apple-green'
                    }`}>
                      {country.unemployment}%
                    </span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm">
                    <span className="font-medium">Handelsbilanz</span>
                    <span className={`font-semibold ${
                      country.tradeSurplus > 0 ? 'text-apple-green' : 'text-apple-red'
                    }`}>
                      ${country.tradeSurplus > 0 ? '+' : ''}{country.tradeSurplus} Mrd.
                    </span>
                  </div>

                  <div className="mt-4 pt-4 border-t border-apple-gray-200">
                    <p className="text-sm font-medium text-apple-gray-900 mb-2">Ressourcen:</p>
                    <div className="flex flex-wrap gap-2">
                      {country.resources.map((resource, idx) => (
                        <span 
                          key={idx}
                          className="px-3 py-1 bg-apple-blue/10 text-apple-blue rounded-pill text-xs font-medium"
                        >
                          {resource}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>

        <div className="mt-12 text-center text-apple-gray-400">
          <p className="text-sm">üí° Tipp: Leichtere L√§nder sind gut f√ºr Anf√§nger, schwierigere L√§nder bieten mehr Herausforderung</p>
        </div>
      </div>
    </div>
  );
}

export default CountrySelection;
