import React, { useState } from 'react';

function Login({ onLogin }) {
  const [name, setName] = useState('');
  const [isAnimating, setIsAnimating] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (name.trim()) {
      setIsAnimating(true);
      setTimeout(() => {
        onLogin(name.trim());
      }, 300);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center p-5">
      <div className="card-apple max-w-2xl w-full animate-scale-in">
        <div className="text-center mb-12">
          <h1 className="text-6xl font-bold text-apple-gray-900 mb-4 tracking-tight">
            üåç Econo-Me
          </h1>
          <p className="text-2xl text-apple-gray-400 font-light tracking-tight">
            Trade Wars ‚Äì Wirtschaftssimulation
          </p>
        </div>

        <div className="max-w-md mx-auto">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div>
              <label className="block text-apple-gray-900 font-medium mb-3 text-lg">
                Dein Name
              </label>
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                placeholder="Gib deinen Namen ein..."
                className="input-apple text-lg"
                autoFocus
              />
            </div>
            <button 
              type="submit" 
              className={`btn-apple w-full text-lg py-4 ${isAnimating ? 'scale-95' : ''}`}
              disabled={!name.trim()}
            >
              Spiel starten üöÄ
            </button>
          </form>

          <div className="mt-12 bg-apple-gray-50 rounded-apple p-8 border border-black/5">
            <h3 className="text-xl font-semibold text-apple-gray-900 mb-5">
              üéØ Spielziel
            </h3>
            <ul className="space-y-3">
              <li className="flex items-start gap-3 text-apple-gray-400">
                <span className="text-2xl">üìä</span>
                <span>F√ºhre dein Land zum wirtschaftlichen Erfolg</span>
              </li>
              <li className="flex items-start gap-3 text-apple-gray-400">
                <span className="text-2xl">üí∞</span>
                <span>Treffe kluge Entscheidungen √ºber Z√∂lle und Steuern</span>
              </li>
              <li className="flex items-start gap-3 text-apple-gray-400">
                <span className="text-2xl">ü§ù</span>
                <span>Manage Beziehungen zu anderen Nationen</span>
              </li>
              <li className="flex items-start gap-3 text-apple-gray-400">
                <span className="text-2xl">üî¨</span>
                <span>Investiere in Technologien und Innovationen</span>
