              <li className="flex items-start gap-3 text-apple-gray-400">
                <span className="text-2xl">üèÜ</span>
                <span>Erreiche die h√∂chste Punktzahl</span>
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
