import React, { useState } from 'react';
import { TrendingUp, TrendingDown, DollarSign, Building2, Factory, Play, ChevronDown, ChevronUp, Ship, Zap, GraduationCap, Globe2, Heart } from 'lucide-react';

function GamePlay({ gameState, onDecisionMade }) {
  const [tariffChange, setTariffChange] = useState(0);
  const [taxChange, setTaxChange] = useState(0);
  const [infrastructure, setInfrastructure] = useState({
    roads: 0,
    education: 0,
    internet: 0,
    energy: 0,
    healthcare: 0
  });
  const [subsidies, setSubsidies] = useState(0);
  const [showInfraDetails, setShowInfraDetails] = useState(false);

  const infrastructureTypes = [
    { id: 'roads', name: 'Straßen & Verkehr', icon: Ship, benefit: 'Handel +5%' },
    { id: 'education', name: 'Bildungssystem', icon: GraduationCap, benefit: 'Arbeitslosigkeit -0.2%' },
    { id: 'internet', name: 'Digitale Infrastruktur', icon: Globe2, benefit: 'Innovation +10%' },
    { id: 'energy', name: 'Energieversorgung', icon: Zap, benefit: 'Nachhaltigkeit +10%' },
    { id: 'healthcare', name: 'Gesundheitswesen', icon: Heart, benefit: 'Produktivität +5%' }
  ];

  const handleSubmit = () => {
    const totalInfra = Object.values(infrastructure).reduce((sum, val) => sum + val, 0);

    onDecisionMade({
      tariffChange,
      taxChange,
      infrastructure: totalInfra, // Legacy support
      infrastructureDetails: infrastructure,
      subsidies
    });
  };

  const handleInfraChange = (type, value) => {
    setInfrastructure(prev => ({
      ...prev,
      [type]: value
    }));
  };

  const totalInfrastructure = Object.values(infrastructure).reduce((sum, val) => sum + val, 0);

  const newTariff = Math.max(0, Math.min(50, gameState.playerCountry.tariffRate + tariffChange));
  const newTax = Math.max(0, Math.min(60, gameState.playerCountry.taxRate + taxChange));

  const hasChanges = tariffChange !== 0 || taxChange !== 0 || totalInfrastructure > 0 || subsidies > 0;

  return (
    <div className="min-h-screen p-5">
      <div className="max-w-5xl mx-auto">
        <div className="text-center mb-12 animate-slide-down">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-apple-green to-green-600 rounded-3xl mb-6 shadow-apple-lg">
            <Play className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-5xl font-bold text-apple-gray-900 mb-4 tracking-tight">
            Runde {gameState.round}
          </h1>
          <p className="text-2xl text-apple-gray-400 font-light tracking-tight flex items-center justify-center gap-2">
            {gameState.playerCountry.name}
            <span className="text-apple-gray-300">•</span>
            <span className="flex items-center gap-1">
              <BarChart3 className="w-5 h-5" />
              BIP: ${Math.round(gameState.playerCountry.gdp)} Mrd.
            </span>
          </p>
        </div>

        <div className="card-apple mb-6 animate-fade-in">
          <h3 className="text-2xl font-semibold text-apple-gray-900 mb-8 tracking-tight flex items-center gap-2">
            <TrendingUp className="w-6 h-6 text-apple-blue" />
            Wirtschaftspolitische Maßnahmen
          </h3>

          <div className="space-y-8">
            {/* Tariff Slider */}
            <div>
              <div className="flex justify-between items-center mb-4">
                <label className="text-lg font-medium text-apple-gray-900 flex items-center gap-2">
                  <DollarSign className="w-5 h-5 text-apple-blue" />
                  Zollsatz ändern
                </label>
                <div className="text-right">
                  <span className={`text-2xl font-bold flex items-center gap-1 ${
                    tariffChange > 0 ? 'text-apple-red' : tariffChange < 0 ? 'text-apple-green' : 'text-apple-gray-400'
                  }`}>
                    {tariffChange > 0 ? <TrendingUp className="w-6 h-6" /> : tariffChange < 0 ? <TrendingDown className="w-6 h-6" /> : null}
                    {tariffChange > 0 ? '+' : ''}{tariffChange}%
                  </span>
                  <div className="text-sm text-apple-gray-400 mt-1">
                    {gameState.playerCountry.tariffRate}% → {newTariff}%
                  </div>
                </div>
              </div>
              <input
                type="range"
                min="-10"
                max="10"
                value={tariffChange}
                onChange={(e) => setTariffChange(Number(e.target.value))}
                className="w-full h-2 bg-apple-gray-200 rounded-full appearance-none cursor-pointer slider"
                style={{
                  background: `linear-gradient(to right, #34c759 0%, #d2d2d7 50%, #ff3b30 100%)`,
                }}
              />
              <p className="text-sm text-apple-gray-400 mt-3 flex items-center gap-2">
                <TrendingUp className="w-4 h-4" />
                Höhere Zölle schützen heimische Industrie, können aber Handelspartner verärgern
              </p>
            </div>

            {/* Tax Slider */}
            <div>
              <div className="flex justify-between items-center mb-4">
                <label className="text-lg font-medium text-apple-gray-900 flex items-center gap-2">
                  <DollarSign className="w-5 h-5 text-apple-green" />
                  Steuersatz ändern
                </label>
                <div className="text-right">
                  <span className={`text-2xl font-bold flex items-center gap-1 ${
                    taxChange > 0 ? 'text-apple-red' : taxChange < 0 ? 'text-apple-green' : 'text-apple-gray-400'
                  }`}>
                    {taxChange > 0 ? <TrendingUp className="w-6 h-6" /> : taxChange < 0 ? <TrendingDown className="w-6 h-6" /> : null}
                    {taxChange > 0 ? '+' : ''}{taxChange}%
                  </span>
                  <div className="text-sm text-apple-gray-400 mt-1">
                    {gameState.playerCountry.taxRate}% → {newTax}%
                  </div>
                </div>
              </div>
              <input
                type="range"
                min="-10"
                max="10"
                value={taxChange}
                onChange={(e) => setTaxChange(Number(e.target.value))}
                className="w-full h-2 bg-apple-gray-200 rounded-full appearance-none cursor-pointer slider"
                style={{
                  background: `linear-gradient(to right, #34c759 0%, #d2d2d7 50%, #ff3b30 100%)`,
                }}
              />
              <p className="text-sm text-apple-gray-400 mt-3 flex items-center gap-2">
                <DollarSign className="w-4 h-4" />
                Höhere Steuern bedeuten mehr Staatseinnahmen, aber weniger Wirtschaftswachstum
              </p>
            </div>

            {/* Infrastructure Slider */}
            <div>
              <div className="flex justify-between items-center mb-4">
                <label className="text-lg font-medium text-apple-gray-900 flex items-center gap-2">
                  <Building2 className="w-5 h-5 text-apple-blue" />
                  Infrastruktur-Investitionen
                </label>
                <button
                  onClick={() => setShowInfraDetails(!showInfraDetails)}
                  className="text-sm text-apple-blue hover:text-apple-blue-dark font-medium flex items-center gap-1 transition-all"
                >
                  {showInfraDetails ? (
                    <>
                      <ChevronUp className="w-4 h-4" />
                      Details ausblenden
                    </>
                  ) : (
                    <>
                      <ChevronDown className="w-4 h-4" />
                      Details anzeigen
                    </>
                  )}
                </button>
              </div>

              {!showInfraDetails ? (
                <div>
                  <div className="flex justify-between items-center mb-4">
                    <span className="text-2xl font-bold text-apple-blue">
                      Gesamt: Level {totalInfrastructure}
                    </span>
                  </div>
                  <p className="text-sm text-apple-gray-400 flex items-center gap-2">
                    <ChevronDown className="w-4 h-4" />
                    Öffne Details um gezielt in verschiedene Bereiche zu investieren
                  </p>
                </div>
              ) : (
                <div className="space-y-6 bg-apple-gray-50 p-5 rounded-apple-sm">
                  {infrastructureTypes.map((type) => {
                    const IconComponent = type.icon;
                    return (
                      <div key={type.id} className="group">
                        <div className="flex justify-between items-center mb-3">
                          <div className="flex items-center gap-3">
                            <div className="p-2 bg-white rounded-xl group-hover:bg-apple-blue/10 transition-colors">
                              <IconComponent className="w-5 h-5 text-apple-blue" />
                            </div>
                            <div>
                              <label className="font-medium text-apple-gray-900">
                                {type.name}
                              </label>
                              <p className="text-xs text-apple-gray-400">{type.benefit}</p>
                            </div>
                          </div>
                          <span className="text-xl font-bold text-apple-blue">
                            Level {infrastructure[type.id]}
                          </span>
                        </div>
                        <input
                          type="range"
                          min="0"
                          max="5"
                          value={infrastructure[type.id]}
                          onChange={(e) => handleInfraChange(type.id, Number(e.target.value))}
                          className="w-full h-2 bg-apple-gray-200 rounded-full appearance-none cursor-pointer slider"
                        />
                      </div>
                    );
                  })}
                  <div className="pt-4 border-t border-apple-gray-200">
                    <div className="flex justify-between items-center">
                      <span className="font-semibold text-apple-gray-900">Gesamtinvestition:</span>
                      <span className="text-2xl font-bold text-apple-blue">
                        Level {totalInfrastructure}
                      </span>
                    </div>
                  </div>
                </div>
              )}
              <p className="text-sm text-apple-gray-400 mt-3 flex items-center gap-2">
                <Building2 className="w-4 h-4" />
                Infrastruktur-Investitionen fördern langfristiges Wachstum in spezifischen Bereichen
              </p>
            </div>

            {/* Subsidies Slider */}
            <div>
              <div className="flex justify-between items-center mb-4">
                <label className="text-lg font-medium text-apple-gray-900 flex items-center gap-2">
                  <Factory className="w-5 h-5 text-apple-orange" />
                  Subventionen
                </label>
                <span className="text-2xl font-bold text-apple-orange">
                  Level {subsidies}
                </span>
              </div>
              <input
                type="range"
                min="0"
                max="10"
                value={subsidies}
                onChange={(e) => setSubsidies(Number(e.target.value))}
                className="w-full h-2 bg-apple-gray-200 rounded-full appearance-none cursor-pointer slider"
              />
              <p className="text-sm text-apple-gray-400 mt-3 flex items-center gap-2">
                <Factory className="w-4 h-4" />
                Unterstütze Schlüsselindustrien und schaffe Arbeitsplätze
              </p>
            </div>
          </div>
        </div>

        {/* Preview Card */}
        <div className={`card-apple border-2 transition-all duration-300 animate-fade-in ${
          hasChanges ? 'border-apple-blue bg-apple-blue/5' : 'border-apple-gray-200 bg-apple-gray-50'
        }`}>
          <h3 className="text-2xl font-semibold text-apple-gray-900 mb-6 tracking-tight flex items-center gap-2">
            <TrendingUp className="w-6 h-6 text-apple-blue" />
            Vorschau deiner Politik
          </h3>

          {hasChanges ? (
            <ul className="space-y-3">
              {tariffChange !== 0 && (
                <li className="flex items-center gap-3 text-lg">
                  <div className="p-2 bg-apple-blue/10 rounded-xl">
                    <DollarSign className="w-5 h-5 text-apple-blue" />
                  </div>
                  <span className="text-apple-gray-900">
                    Zölle: <strong>{gameState.playerCountry.tariffRate}% → {newTariff}%</strong>
                  </span>
                </li>
              )}
              {taxChange !== 0 && (
                <li className="flex items-center gap-3 text-lg">
                  <div className="p-2 bg-apple-green/10 rounded-xl">
                    <DollarSign className="w-5 h-5 text-apple-green" />
                  </div>
                  <span className="text-apple-gray-900">
                    Steuern: <strong>{gameState.playerCountry.taxRate}% → {newTax}%</strong>
                  </span>
                </li>
              )}
              {totalInfrastructure > 0 && (
                <li className="flex items-start gap-3 text-lg">
                  <div className="p-2 bg-apple-orange/10 rounded-xl">
                    <Building2 className="w-5 h-5 text-apple-orange" />
                  </div>
                  <span className="text-apple-gray-900">
                    Infrastruktur-Investitionen: <strong>Level {totalInfrastructure}</strong>
                    {showInfraDetails && (
                      <span className="block text-sm text-apple-gray-400 mt-1">
                        {Object.entries(infrastructure)
                          .filter(([_, val]) => val > 0)
                          .map(([key, val]) => {
                            const type = infrastructureTypes.find(t => t.id === key);
                            const IconComp = type?.icon;
                            return (
                              <span key={key} className="inline-flex items-center gap-1 mr-2">
                                {IconComp && <IconComp className="w-3 h-3" />}
                                {type?.name}: Level {val}
                              </span>
                            );
                          })}
                      </span>
                    )}
                  </span>
                </li>
              )}
              {subsidies > 0 && (
                <li className="flex items-center gap-3 text-lg">
                  <div className="p-2 bg-apple-blue/10 rounded-xl">
                    <Factory className="w-5 h-5 text-apple-blue" />
                  </div>
                  <span className="text-apple-gray-900">
                    Subventionen: <strong>Level {subsidies}</strong>
                  </span>
                </li>
              )}
            </ul>
          ) : (
            <p className="text-apple-gray-400 text-center py-4">
              Keine Änderungen - Status Quo beibehalten
            </p>
          )}
        </div>

        <div className="mt-8 text-center">
          <button 
            onClick={handleSubmit} 
            className="btn-apple text-xl py-5 px-12 shadow-apple-lg hover:scale-105 transition-transform flex items-center gap-3 mx-auto"
          >
            <Play className="w-6 h-6" />
            Entscheidungen umsetzen
          </button>
        </div>
      </div>
    </div>
  );
}

export default GamePlay;
