import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import CountrySelection from './components/CountrySelection';
import Dashboard from './components/Dashboard';
import GamePlay from './components/GamePlay';
import Tutorial from './components/Tutorial';
import Modal from './components/Modal';

function App() {
  const [screen, setScreen] = useState('login'); // login, tutorial, country-select, dashboard, gameplay
  const [showTutorial, setShowTutorial] = useState(false);
  const [feedbackModal, setFeedbackModal] = useState({ isOpen: false, title: '', message: '' });
  const [playerName, setPlayerName] = useState('');
  const [gameId, setGameId] = useState(null);
  const [gameState, setGameState] = useState(null);
  const [countries, setCountries] = useState({});

  useEffect(() => {
    // Load available countries
    fetch('/api/countries')
      .then(res => {
        if (!res.ok) {
          throw new Error('Fehler beim Laden der Länder');
        }
        return res.json();
      })
      .then(data => {
        console.log('Länder geladen:', data);
        setCountries(data);
      })
      .catch(err => {
        console.error('Error loading countries:', err);
        setFeedbackModal({
          isOpen: true,
          title: 'Verbindungsfehler',
          message: 'Fehler beim Laden der Länder. Bitte stelle sicher, dass der Server läuft.'
        });
      });
  }, []);

  const handleLogin = (name) => {
    setPlayerName(name);
    setShowTutorial(true);
  };

  const handleTutorialComplete = () => {
    setShowTutorial(false);
    setScreen('country-select');
  };

  const handleCountrySelect = async (countryId) => {
    try {
      const response = await fetch('/api/game/create', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ playerName, countryId })
      });

      if (!response.ok) {
        throw new Error('Fehler beim Erstellen des Spiels');
      }

      const data = await response.json();
      setGameId(data.gameId);
      setGameState(data.game);
      setScreen('dashboard');
    } catch (err) {
      console.error('Error creating game:', err);
      setFeedbackModal({
        isOpen: true,
        title: 'Fehler beim Spielstart',
        message: 'Fehler beim Laden des Spiels. Bitte versuche es erneut.\n\nStelle sicher, dass der Server läuft (Port 3001).'
      });
    }
  };

  const handleStartRound = () => {
    setScreen('gameplay');
  };

  const handleDecisionMade = async (decisions) => {
    try {
      const response = await fetch(`/api/game/${gameId}/decision`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(decisions)
      });
      const data = await response.json();
      setGameState(data.game);
      setScreen('dashboard');

      // Show custom feedback modal instead of alert
      if (data.roundResult && data.roundResult.feedback) {
        const feedbackText = data.roundResult.feedback
          .map(item => typeof item === 'string' ? item : `${item.message}\n\n${item.explanation || ''}`)
          .join('\n\n---\n\n');

        setFeedbackModal({
          isOpen: true,
          title: `Runde ${data.game.round} - Ergebnisse`,
          message: feedbackText
        });
      }
    } catch (err) {
      console.error('Error submitting decision:', err);
      setFeedbackModal({
        isOpen: true,
        title: 'Fehler',
        message: 'Entscheidungen konnten nicht übermittelt werden. Bitte versuche es erneut.'
      });
    }
  };

  const refreshGameState = async () => {
    if (!gameId) return;
    try {
      const response = await fetch(`/api/game/${gameId}`);
      const data = await response.json();
      setGameState(data);
    } catch (err) {
      console.error('Error refreshing game:', err);
    }
  };

  return (
    <div className="App">
      {screen === 'login' && (
        <Login onLogin={handleLogin} />
      )}

      {showTutorial && (
        <Tutorial onComplete={handleTutorialComplete} />
      )}

      {screen === 'country-select' && (
        <CountrySelection 
          countries={countries}
          onSelect={handleCountrySelect}
        />
      )}
      {screen === 'dashboard' && gameState && (
        <Dashboard 
          gameState={gameState}
          onStartRound={handleStartRound}
          onRefresh={refreshGameState}
        />
      )}
      {screen === 'gameplay' && gameState && (
        <GamePlay 
          gameState={gameState}
          onDecisionMade={handleDecisionMade}
        />
      )}

      {/* Custom Feedback Modal */}
      <Modal
        isOpen={feedbackModal.isOpen}
        onClose={() => setFeedbackModal({ ...feedbackModal, isOpen: false })}
        title={feedbackModal.title}
      >
        <div className="space-y-4">
          {feedbackModal.message.split('---').map((section, index) => (
            <div key={index} className="text-apple-gray-600 leading-relaxed">
              {section.split('\n\n').map((paragraph, pIndex) => (
                <p key={pIndex} className="mb-3">
                  {paragraph}
                </p>
              ))}
            </div>
          ))}
          <button
            onClick={() => setFeedbackModal({ ...feedbackModal, isOpen: false })}
            className="btn-apple w-full mt-6"
          >
            Verstanden
          </button>
        </div>
      </Modal>
    </div>
  );
}

export default App;
