import React, { useState } from 'react';
import { Globe, TrendingUp, TrendingDown, Users, DollarSign, BarChart3, Star } from 'lucide-react';

function CountrySelection({ countries, onSelect }) {
  const [selectedCountry, setSelectedCountry] = useState(null);
  const [hoveredCountry, setHoveredCountry] = useState(null);
  const [isLoading, setIsLoading] = useState(false);

  const getFlag = (countryId) => {
    const flags = {
      'Germany': 'üá©üá™',
      'USA': 'üá∫üá∏',
      'China': 'üá®üá≥',
      'Japan': 'üáØüáµ',
      'UK': 'üá¨üáß',
      'France': 'üá´üá∑'
    };
    return flags[countryId] || 'üè≥Ô∏è';
  };

  const getDifficulty = (country) => {
    const unemploymentScore = country.unemployment > 6 ? 'Schwer' : country.unemployment > 4 ? 'Mittel' : 'Leicht';

    if (unemploymentScore === 'Schwer' || country.tradeSurplus < -500) return { level: 'Schwer', color: 'text-apple-red' };
    if (unemploymentScore === 'Mittel') return { level: 'Mittel', color: 'text-apple-orange' };
    return { level: 'Leicht', color: 'text-apple-green' };
  };

  const handleSelect = (countryId) => {
    setSelectedCountry(countryId);
    setIsLoading(true);
    setTimeout(() => {
      onSelect(countryId);
    }, 300);
  };

  return (
    <div className="min-h-screen p-5">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16 animate-slide-down">
          <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-apple-blue to-blue-600 rounded-3xl mb-6 shadow-apple-lg">
            <Globe className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-6xl font-bold text-apple-gray-900 mb-4 tracking-tight">
            W√§hle dein Land
          </h1>
          <p className="text-2xl text-apple-gray-400 font-light tracking-tight">
            Jedes Land hat unterschiedliche St√§rken und Herausforderungen
          </p>
        </div>

        {Object.keys(countries).length === 0 ? (
          <div className="text-center py-20">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-apple-blue/10 rounded-2xl mb-4 animate-pulse">
              <Globe className="w-8 h-8 text-apple-blue" />
            </div>
            <p className="text-2xl text-apple-gray-400">L√§nder werden geladen...</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {Object.entries(countries).map(([id, country], index) => {
            const difficulty = getDifficulty(country);
            const isSelected = selectedCountry === id;
            const isHovered = hoveredCountry === id;

            return (
              <div
                key={id}
                className={`card-apple cursor-pointer transition-all duration-300 hover:-translate-y-1 ${
                  isSelected ? 'ring-4 ring-apple-blue scale-105 shadow-apple-lg' : ''
                } ${isHovered ? 'shadow-apple-lg' : ''}`}
                style={{ animationDelay: `${index * 50}ms` }}
                onClick={() => handleSelect(id)}
                onMouseEnter={() => setHoveredCountry(id)}
                onMouseLeave={() => setHoveredCountry(null)}
              >
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-3xl font-semibold text-apple-gray-900 tracking-tight flex items-center gap-3">
                    <span className="text-4xl">{getFlag(id)}</span>
                    {country.name}
                  </h3>
                  <span className={`text-sm font-medium px-3 py-1.5 rounded-pill flex items-center gap-1.5 ${difficulty.color} bg-opacity-10`}>
                    <Star className="w-4 h-4" />
                    {difficulty.level}
                  </span>
                </div>

                <div className="space-y-3 text-apple-gray-400">
                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm hover:bg-apple-blue/5 transition-colors">
                    <span className="font-medium flex items-center gap-2">
                      <BarChart3 className="w-4 h-4 text-apple-blue" />
                      BIP
                    </span>
                    <span className="font-semibold text-apple-gray-900">${country.gdp} Mrd.</span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm hover:bg-apple-green/5 transition-colors">
                    <span className="font-medium flex items-center gap-2">
                      <Users className="w-4 h-4 text-apple-green" />
                      Bev√∂lkerung
                    </span>
                    <span className="font-semibold text-apple-gray-900">{country.population} Mio.</span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm hover:bg-apple-orange/5 transition-colors">
                    <span className="font-medium flex items-center gap-2">
                      {country.unemployment > 6 ? <TrendingDown className="w-4 h-4 text-apple-red" /> : <TrendingUp className="w-4 h-4 text-apple-green" />}
                      Arbeitslosigkeit
                    </span>
                    <span className={`font-semibold ${
                      country.unemployment > 6 ? 'text-apple-red' : 
                      country.unemployment > 4 ? 'text-apple-orange' : 
                      'text-apple-green'
                    }`}>
                      {country.unemployment}%
                    </span>
                  </div>

                  <div className="flex justify-between items-center p-3 bg-apple-gray-50 rounded-apple-sm hover:bg-apple-blue/5 transition-colors">
                    <span className="font-medium flex items-center gap-2">
                      <DollarSign className="w-4 h-4 text-apple-blue" />
                      Handelsbilanz
                    </span>
                    <span className={`font-semibold flex items-center gap-1 ${
                      country.tradeSurplus > 0 ? 'text-apple-green' : 'text-apple-red'
                    }`}>
                      {country.tradeSurplus > 0 ? <TrendingUp className="w-4 h-4" /> : <TrendingDown className="w-4 h-4" />}
                      ${country.tradeSurplus > 0 ? '+' : ''}{country.tradeSurplus} Mrd.
                    </span>
                  </div>

                  <div className="mt-4 pt-4 border-t border-apple-gray-200">
                    <p className="text-sm font-medium text-apple-gray-900 mb-2">Ressourcen:</p>
                    <div className="flex flex-wrap gap-2">
                      {country.resources.map((resource, idx) => (
                        <span 
                          key={idx}
                          className="px-3 py-1 bg-apple-blue/10 text-apple-blue rounded-pill text-xs font-medium"
                        >
                          {resource}
                        </span>
                      ))}
                    </div>
                  </div>
                </div>
              </div>
              );
            })}
          </div>
        )}

        <div className="mt-12 text-center text-apple-gray-400">
          <p className="text-sm flex items-center justify-center gap-2">
            <Star className="w-4 h-4 text-apple-blue" />
            Tipp: Leichtere L√§nder sind gut f√ºr Anf√§nger, schwierigere L√§nder bieten mehr Herausforderung
          </p>
        </div>        {isLoading && (
          <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 animate-fade-in">
            <div className="card-apple text-center max-w-md">
              <div className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-br from-apple-blue to-blue-600 rounded-3xl mb-4 animate-pulse">
                <Globe className="w-10 h-10 text-white" />
              </div>
              <h2 className="text-3xl font-bold text-apple-gray-900 mb-2">Spiel wird geladen...</h2>
              <p className="text-apple-gray-400">Dein Land wird vorbereitet</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CountrySelection;
